CREATE TABLE "public"."plip_signatures" ("id" serial NOT NULL, "widget_id" integer NOT NULL, "confirmed_signatures" integer NOT NULL, "user_id" integer NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "unique_identifier" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("widget_id") REFERENCES "public"."widgets"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict);COMMENT ON TABLE "public"."plip_signatures" IS E'Tabela responsável por armazenar assinaturas confirmadas em formulários PLIP';
